mapper.update:
UPDATE lab_result_detail
document_id = #{documentId}, result_date = #{resultDate,jdbcType=DATE}, test_name = #{testName}, result_value = #{resultValue}, unit = #{unit}, ref_low = #{refLow}, ref_high = #{refHigh}, flag = #{flag}, notes = #{notes}
WHERE id = #{id}

mapper.select:
SELECT id, document_id, result_date, test_name, result_value, unit, ref_low, ref_high, flag, notes
FROM lab_result_detail
WHERE id = #{id}
