mapper.select:
SELECT md.id AS document_id, md.visit_id AS visit_id, md.doc_type AS doc_type, md.file_url AS file_url, md.ocr_json AS ocr_json,
v.pet_id AS v_pet_id, v.visit_date AS visit_date, v.hospital AS hospital, v.doctor AS doctor, v.diagnosis AS diagnosis, v.notes AS notes,
p.id AS p_id, p.name AS pet_name, -- ✓ pet.name p.species AS species, -- ✓ pet.species p.gender AS sex, -- ✓ pet.gender → sex 로 별칭 p.birthDate AS birth_date, -- ✓ pet.birthDate → birth_date 로 별칭 NULL AS age, -- (스키마에 없으니 null) NULL AS age_months -- (스키마에 없으니 null) FROM medical_document md JOIN visit v ON v.id = md.visit_id LEFT JOIN pet p ON p.id = v.pet_id WHERE md.id = #{docId}

SELECT id, visit_id, doc_type, file_url, ocr_json, created_at
FROM medical_document
WHERE id = #{id}
