mapper.select:
SELECT *
FROM pet p
WHERE p.memberId = #{memberId}

SELECT *
FROM pet p
WHERE p.id = #{petId}

SELECT COUNT(*)
FROM pet_weight_log
WHERE petId = #{petId}

mapper.update:
UPDATE pet
SET weight = #{weightKg}
WHERE id = #{petId}

mapper.select:
SELECT id
FROM pet
WHERE memberId = #{memberId}
AND name = #{name}
ORDER BY id DESC
LIMIT 1

SELECT COUNT(*)
FROM
pet_feed_log
WHERE petId = #{petId}
AND DATE(fedAt) = #{ymd}
