mapper.select:
SELECT COUNT(*)
FROM walk_crew_member
WHERE memberId = #{memberId}
AND crewId =
#{crewId}

SELECT COUNT(*)
FROM walk_crew_member
WHERE crewId = #{crewId}
AND
memberId = #{memberId}
AND status = 'approved'

mapper.delete:
DELETE FROM
walk_crew_member
WHERE crewId = #{crewId} AND memberId = #{memberId}
